      },
      complexMock() {
        this.key = 'key_' + Math.random()
        let jsonObj = {
          name: '张三',
          age: 12,
          classMate: [
            {
              id: 1001,
              name: '李四',
            },
            {
              id: 1002,
              name: 'jack ma',
            },
          ],
        }
        this.data = jsonObj
      },
      numberMock() {
        this.key = 'key_' + Math.random()
        this.data = 10011
      },
      floatMock() {
        this.key = 'key_' + Math.random()
        this.data = 3.1415926535893384626
      },

      keyChange: function (e : InputEvent) {
        this.key = e.detail.value
      },
      dataChange: function (e : InputEvent) {
        this.data = e.detail.value
      },
      getStorage: function () {
        var key = this.key
        if (key.length == 0) {
          uni.showModal({
            title: '读取数据失败',
            content: 'key 不能为空',
            showCancel: false,
          })
        } else {
          let that = this
          uni.getStorage({
            key: key,
            success: (res) => {

              that.apiGetData = res.data
              let desc : string = typeof this.apiGetData
              if ("object" == desc) {
                desc = desc + ": " + JSON.stringify(this.apiGetData)
              } else {
                desc = desc + ": " + this.apiGetData
              }

              uni.showModal({
                title: '读取数据成功',
                content: desc,
                showCancel: false,
              })
            },
            fail: () => {
              uni.showModal({
                title: '读取数据失败',
                content: '找不到 key 对应的数据',
                showCancel: false,
              })
            },
          })
        }
      },
      getStorageSync: function () {
        var key = this.key
        if (key.length == 0) {
          uni.showModal({
            title: '读取数据失败',
            content: 'key 不能为空',
            showCancel: false,
          })
        } else {
          this.apiGetData = uni.getStorageSync(key)

          let desc : string = typeof this.apiGetData
          if ("object" == desc) {
            desc = desc + ": " + JSON.stringify(this.apiGetData)
          } else {
            desc = desc + ": " + this.apiGetData
          }

          uni.showModal({
            title: '读取数据成功',
            content: desc,
            showCancel: false,
          })
        }
      },
      setStorage: function () {
        var key = this.key
        var data = this.data
        if (key.length == 0) {
          uni.showModal({
            title: '保存数据失败',
            content: 'key 不能为空',
            showCancel: false,
          })
        } else {
          uni.setStorage({
            key: key,
            data: data,
            success: () => {
              uni.showModal({
                title: '存储数据成功',
                showCancel: false,
              })
            },
            fail: () => {
              uni.showModal({
                title: '储存数据失败!',
                showCancel: false,
              })
            },
          })
        }
      },
      setStorageSync: function () {
        var key = this.key
        var data = this.data
        if (key.length == 0) {
          uni.showModal({
            title: '保存数据失败',
            content: 'key 不能为空',
            showCancel: false,
          })
        } else {
          uni.setStorageSync(key, data)
          uni.showModal({
            title: '存储数据成功',
            showCancel: false,
          })
        }
      },
      removeStorage: function () {
        uni.removeStorage({
          key: this.key,
          success: () => {
            uni.showModal({
              title: '移除数据成功',
              showCancel: false,
            })
          },
          fail: () => {
            uni.showModal({
              title: '移除数据失败',
              showCancel: false,
            })
          },
        })
      },
      removeStorageSync: function () {
        uni.removeStorageSync(this.key)
        uni.showModal({
          title: '移除数据成功',
          showCancel: false,
        })
      },
      clearStorage: function () {
        this.key = ''
        this.data = ''
        uni.clearStorage({
          success: function (_) {
            uni.showModal({
              title: '清除数据成功',
              showCancel: false,
            })
          },
          fail: function (_) {
            uni.showModal({
              title: '清除数据失败',
              showCancel: false,
            })
          },
        })
      },
      clearStorageSync: function () {
        this.key = ''
        this.data = ''
        uni.clearStorageSync()
        uni.showModal({
          title: '清除数据成功',
          content: ' ',
          showCancel: false,
        })
      },
    },
  }

