              showCancel: false
            })
          },
          fail: () => {
            uni.showModal({
              content: '读取剪贴板失败!',
              showCancel: false
            })
          }
        });
      },
      setClipboard: function () {
        if (this.data.length === 0) {
          uni.showModal({
            title: '设置剪贴板失败',
            content: '内容不能为空',
            showCancel: false
          })
        } else {
          uni.setClipboardData({
