            success: () => {
              this.setClipboardTest = true
              // 成功处理
              uni.showToast({
                title: '设置剪贴板成功',
                icon: "success",
                mask: !1
              })
            },
            fail: () => {
              // bug：自动化测试时设置成功也进入了fail
              this.setClipboardTest = false
              // 失败处理
              uni.showToast({
                title: '储存数据失败!',
                icon: "none",
                mask: !1
              })
            }
          });
        }
      }
    }
