```uts

  export default {
    data() {
      return {
        msg: '点击按钮，测试上报'
      }
    },
    methods: {
      handleAppLaunch() {
        const options = uni.getLaunchOptionsSync()
        uni.report({
          name: 'uni-app-launch',
          options: options,
          success:(res)=> {
            this.msg = 'onLaunch --> ' + res.errMsg
            console.log(res);
          }, fail:(err)=>  {
            this.msg = 'onLaunch --> ' + err.errMsg
            console.log(err);
          }
        })
      },
      handleAppHide() {
        uni.report({
          name: 'uni-app-hide',
          success:(res)=> {
            this.msg = 'onAppHide --> ' + res.errMsg
            console.log(res);
          }, fail:(err)=>  {
            this.msg = 'onAppHide --> ' + err.errMsg
            console.log(err);
          }
        })
      },
      handleAppShow() {
        // const options = uni.getLaunchOptionsSync()
        uni.report({
          name: 'uni-app-show',
          success:(res)=> {
            this.msg = 'onAppShow --> ' + res.errMsg
            console.log(res);
          }, fail:(err)=>  {
            this.msg = 'onAppShow --> ' + err.errMsg
            console.log(err);
          }
        })
      },
      handleAppError() {
        const errmsg = '测试错误'
        uni.report({
          name: 'uni-app-error',
          options: errmsg,
          success:(res)=> {
            this.msg = 'onAppError --> ' + res.errMsg
            console.log(res);
          }, fail:(err)=>  {
            this.msg = 'onAppError --> ' + err.errMsg
            console.log(err);
          }
        })
      },
    }
  }

