    methods: {
      add: function () {
        this.extraLine.push(this.texts[this.extraLine.length % 12]);
        this.text = this.extraLine.join('\n');
        this.canAdd = this.extraLine.length < 12;
        this.canRemove = this.extraLine.length > 0;
      },
      remove: function () {
        if (this.extraLine.length > 0) {
          this.extraLine.pop();
          this.text = this.extraLine.join('\n');
          this.canAdd = this.extraLine.length < 12;
          this.canRemove = this.extraLine.length > 0;
        }
      },
      textProps: function () {
