  export default {
    data() {
      return {
        x: 0,
        y: 0,
        scale: 2,
        old: {
          x: 0,
          y: 0,
          scale: 2
        }
      }
    },
    methods: {
      tap: function (e) {
        // 解决view层不同步的问题
        this.x = this.old.x
        this.y = this.old.y
        this.$nextTick(function () {
          this.x = 30
          this.y = 30
        })
      },
      tap2() {
        // 解决view层不同步的问题
        this.scale = this.old.scale
        this.scale = this.old.scale
        this.$nextTick(function () {
          this.scale = 3
        })
      },
      onChange: function (e) {
        this.old.x = e.detail.x
        this.old.y = e.detail.y
      },
      onScale: function (e) {
        this.old.scale = e.detail.scale
      }
    }
  }

