  type Context = {
    id ?: string;
    pageId ?: number;
  }

  export default {
    data() {
      return {
        readOnly: false,
        formats: {},
        editorCtx: {} as Context,
        // 自动化测试
        autoTest: false,
        undoTest: false,
        redoTest: false,
        removeFormatTest: false,
        insertImageTest: false,
        blurTest: false
      }
    },
    onLoad() {
      uni.loadFontFace({
        family: 'Pacifico',
        source: 'url("/static/font/Pacifico-Regular.ttf")',
        success() {
          console.log('success load font')
        },
        fail() {
          console.log('fail load font load')
        }
      })
    },
    methods: {
      readOnlyChange() {
        this.readOnly = !this.readOnly
      },
      onEditorReady() {
        uni.createSelectorQuery().select('#editor').context((res) => {
          this.editorCtx = res.context
        }).exec()
      },
      // 自动化测试专用
      setContents(options) {
        this.editorCtx.setContents({
          delta: {
            ops: options
          },
          success: (res) => {
            console.log('setContents-success', res)
          },
          fail: (err) => {
            console.log(err)
          }
        })
      },
      blur() {
        this.editorCtx.blur({
          success: (res) => {
            console.log('编辑器失焦：', res)
            this.blurTest = true
          },
          fail: (err) => {
            console.log(err)
          }
        })
      },
      getCon() {
        this.editorCtx.getContents({
          success: (res) => {
            console.log('文本详情：', res)
          },
          fail: (err) => {
            console.log(err)
          }
        })
      },
      undo() {
        this.editorCtx.undo({
          success: (res) => {
            this.undoTest = true
          },
          fail: (err) => {
            this.undoTest = false
          }
        })
      },
      redo() {
        this.editorCtx.redo({
          success: (res) => {
            this.redoTest = true
          },
          fail: (err) => {
            this.redoTest = false
          }
        })
      },
      format(e) {
        let { name, value } = e.target.dataset
        if (!name) return
        // console.log('format', name, value)
        this.editorCtx.format(name, value)
      },
      onStatusChange(e) {
        const formats = e.detail
        this.formats = formats
      },
      insertDivider() {
        this.editorCtx.insertDivider({
          success: function () {
            console.log('insert divider success')
          }
        })
      },
      clear() {
        this.editorCtx.clear({
          success: function (res) {
            console.log("clear success")
          }
        })
      },
      clearShowModal() {
        uni.showModal({
          title: '清空编辑器',
          content: '确定清空编辑器全部内容？',
          success: res => {
            if (res.confirm) {
              this.clear()
            }
          }
        })
      },
      removeFormat() {
        this.editorCtx.removeFormat({
          success: (res) => {
            console.log('removeFormat-success', res)
            this.removeFormatTest = true
          },
          fail: (err) => {
            this.removeFormatTest = false
          }
        })
      },
      insertDate() {
        const date = new Date()
        const formatDate = `${date.getFullYear()}/${date.getMonth() + 1}/${date.getDate()}`
        this.editorCtx.insertText({
          text: formatDate
        })
      },
      insertImage(image) {
        this.editorCtx.insertImage({
          src: image,
          alt: '图像',
          success: () => {
            console.log('insert image success')
            this.insertImageTest = true
          }
        })
      },
      chooseInsertImage() {
        uni.chooseImage({
          count: 1,
          success: (res) => {
            this.insertImage(res.tempFilePaths[0])
          }
        })
      }
    }
  }

