<script>
  import refreshBox from '../../template/custom-refresher/refresh-box/refresh-box.uvue';
	export default {
    components: { refreshBox },
		data() {
			return {
          scrollData: [] as Array<string>,
          scrollTop: 0,
          refresherTriggered: false,
          pullingDistance: 0,
          resetting: false
			}
		},
    computed: {
    	state() : number {
    		if (this.resetting) {
    			return 3;
    		}
    		if (this.refresherTriggered) {
    			return 2
    		}
    		if (this.pullingDistance > 45) {
    			return 1
    		} else {
    			return 0;
    		}
    	}
    },
    onLoad() {
    	let lists : Array<string> = []
    	for (let i = 0; i < 30; i++) {
    		lists.push("item---" + i)
    	}
    	this.scrollData = lists
    },
		methods: {
      onRefresherpulling(e : RefresherEvent) {
      	this.pullingDistance = e.detail.dy;
      },
      onRefresherrefresh() {
      	this.refresherTriggered = true
      	setTimeout(() => {
      		this.refresherTriggered = false
      		this.resetting = true;
      	}, 1500)
      },
      onRefreshrestore() {
        this.pullingDistance = 0
      	this.resetting = false;
      },
      //自动化测试使用
      testBodyScrollBy(y: number) {
        this.scrollTop = y
      }
		}
	}
</script>

